import logo from './logo.svg';
import './App.css';
import Label from './Components/Label';
import VariableChanger from './Components/VariableComponent';
import Clock from './Components/Clock';
import ControlsGroup from './Components/ControlsGroup';
import React, {useState, useEffect} from 'react';

function App() {
    const [breakT, setBreakT] = useState(5);
    const [session, setSession] = useState(25);
    const [activateClock, setActivateClock] = useState(false);
    const [min, setMin] = useState(session);
    const [sec, setSec] = useState(0);
    const [clockState, setClockState] = useState('Session');

    useEffect(() => {
      let intTick;

      if (activateClock) {
        intTick = setInterval(() => {
          setSec((prevSec) => {
            if (prevSec > 0) {
              return prevSec - 1;
            } else {
              if (min > 0) {
                setMin((prevMin) => prevMin - 1);
                return 59;
              } else {
                console.log('her')
                if(clockState === "Session") setClockState("Break");
                else setClockState("Session");
                reset();
                clearInterval(intTick);
                return 0;
              }
            }
          });
        }, 1000);
      }

      return () => {
        clearInterval(intTick);
      };
    }, [activateClock]);

    function resume(){
      setActivateClock(!activateClock);
    }
    function pause(){
      setActivateClock(!activateClock);
    }
    function reset(){
      setMin(session);
      setSec(0);
      setActivateClock(false);
    }
    function changeInitSession(incdec){
      if(session>=1){
        setSession(session+incdec);
        setMin(session+incdec);
      }
    }
    function changeInitBreak(incdec){
      setBreakT(breakT+incdec);
    }

    return (
      <div className='main-content'>
        <Label text="Pomodoro Clock"/>
        <div className='variable-changer'>
          <VariableChanger text="Choose Break" variable={breakT} changeValue={changeInitBreak}/>
          <VariableChanger text="Choose Session" variable={session} changeValue={changeInitSession}/>   
        </div>
        <Clock min={min} sec={sec} clockState={clockState}/>
        <ControlsGroup resume={resume} pause={pause} reset={reset}/>
      </div>
    );
}

export default App;
