import React,{useEffect, useState} from "react";
import Label from "./Label";

export default function Clock({clockState, time, isActivated, isReset}) {
    let reset = isReset;
    const [min, setMin] = useState(time);
    const [sec, setSec] = useState(0);
    if(isActivated) setInterval(tick, 1000);
    function tick(){
        if(sec>0) setSec(sec - 1);
        else {
            setSec(59)
            setMin(min -1);
        }
    }
    function res() {
        setMin(time);
        setMin(0);
        reset = false;
    }
    return (
        <div>
            <Label text={clockState}/>
            <Label text={min}/>
            <Label text={sec}/>
        </div>
    )
}
