import React, {useState} from "react";
import Label from "./Label";
import IncrementButton from "./IncrementButton";

export default function VariableChanger({text, value}){
    const [variable, setVariable] = useState(value);
    function changeValue(incdec){
        setVariable(variable + incdec);
    }
    return (
        <div>
            <Label text={text}/>
            <IncrementButton type="increment" changeHandler={() => {changeValue(1)}}/>
            <h1>{variable}</h1>
            <IncrementButton type="decrement" changeHandler={() => {changeValue(-1)}}/>
        </div>
    )
}
