import logo from './logo.svg';
import './App.css';
import Label from './Components/Label';
import VariableChanger from './Components/VariableComponent';
import Clock from './Components/Clock';
import ControlsGroup from './Components/ControlsGroup';
import React, {useState, useEffect} from 'react';

function App() {
    const [breakT, setBreakT] = useState(5);
    const [session, setSession] = useState(25);
    const [activateClock, setActivateClock] = useState(false);
    const [resetClock, setResetClock] = useState(false);
    const [min, setMin] = useState(session);
    const [sec, setSec] = useState(0);
    const [clockState, setClockState] = useState('Session');

    useEffect(() => {
      let intTick;

      if (activateClock) {
        intTick = setInterval(() => {
          setSec((prevSec) => {
            if (prevSec > 0) {
              return prevSec - 1;
            } else {
              if (min > 0) {
                setMin((prevMin) => prevMin - 1);
                return 59;
              } else {
                setActivateClock(false);
                setResetClock(true);
                clearInterval(intTick);
                return 0;
              }
            }
          });
        }, 1000);
      }

      return () => {
        clearInterval(intTick);
      };
    }, [activateClock]);
    function tick() {
      if (sec > 0) {
        setSec(sec - 1);
      } else {
        if (min > 0) {
          setSec(59);
          setMin(min - 1);
        } else {
          // If min is already 0, stop the clock
          setActivateClock(false);
          setResetClock(true);
        }
      }
    }

    function resume(){
      setActivateClock(!activateClock);
      setResetClock(false);
    }
    function pause(){
      setActivateClock(!activateClock);
      setResetClock(false);
    }
    function reset(){
      setMin(session);
      setSec(0);
      setActivateClock(false);
    }
    return (
      <div className='main-content'>
        <Label text="Pomodoro Clock"/>
        <div className='variable-changer'>
          <VariableChanger text="Choose Break" value={breakT}/>
          <VariableChanger text="Choose Session" value={session}/>   
        </div>
        <Clock min={min} sec={sec} />
        <ControlsGroup resume={resume} pause={pause} reset={reset}/>
      </div>
    );
}

export default App;
